// THIS CONTRACT'S FUNCTIONALITY IS BASED OFF OF THE OpenZeppelin PaymentSplitter
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract SplitTreasury is AccessControl {
  event PayeeAdded(address account, uint256 shares);
  event PaymentReleased(address to, uint256 amount);
  event PaymentReceived(address from, uint256 amount);

  uint256 private _totalShares;
  uint256 private _totalReleased;
  mapping(address => uint256) private _shares;
  mapping(address => uint256) private _released;
  address[] private _payees;

  bytes32 public constant RELEASER = keccak256("RELEASER");
  bytes32 public constant ADMIN = keccak256("ADMIN");

  constructor(address[] memory payees, uint256[] memory shares_) payable {
    require(payees.length == shares_.length, "PaymentSplitter: payees and shares length mismatch");
    require(payees.length > 0, "PaymentSplitter: no payees");

    for (uint256 i = 0; i < payees.length; i++) {
      _addPayee(payees[i], shares_[i]);
      _setupRole(RELEASER, payees[i]);
    }

    _setRoleAdmin(RELEASER, ADMIN);
    _setupRole(ADMIN, msg.sender);
  }

  receive() external payable {
    emit PaymentReceived(msg.sender, msg.value);
  }

  function totalShares() external view returns (uint256) {
    return _totalShares;
  }

  function totalReleased() external view returns (uint256) {
    return _totalReleased;
  }

  function shares(address account) external view returns (uint256) {
    return _shares[account];
  }

  function released(address account) external view returns (uint256) {
    return _released[account];
  }

  function payee(uint256 index) external view returns (address) {
    return _payees[index];
  }

  function paymentDue(address account) public view returns (uint256) {
    require(_shares[account] > 0, "PaymentSplitter: account has no shares");

    uint256 totalReceived = address(this).balance + _totalReleased;
    return (totalReceived * _shares[account]) / _totalShares - _released[account];
  }

  function release(address payable account) public onlyRole(RELEASER) {
    require(_shares[account] > 0, "PaymentSplitter: account has no shares");

    uint256 totalReceived = address(this).balance + _totalReleased;
    uint256 payment = (totalReceived * _shares[account]) / _totalShares - _released[account];

    require(payment != 0, "PaymentSplitter: account is not due payment");

    _released[account] = _released[account] + payment;
    _totalReleased = _totalReleased + payment;

    emit PaymentReleased(account, payment);
    Address.sendValue(account, payment);
  }

  function releaseAll() external onlyRole(ADMIN) {
    for(uint16 i = 0; i < _payees.length; i++) {
      if(paymentDue(_payees[i]) > 0) {
        release(payable(_payees[i]));
      }
    }
  }

  function _addPayee(address account, uint256 shares_) private {
    require(account != address(0), "PaymentSplitter: account is the zero address");
    require(shares_ > 0, "PaymentSplitter: shares are 0");
    require(_shares[account] == 0, "PaymentSplitter: account already has shares");

    _payees.push(account);
    _shares[account] = shares_;
    _totalShares = _totalShares + shares_;
    emit PayeeAdded(account, shares_);
  }
}","keccak256":"0x2d76b4d34ffa816013414dd7be7d43250505ff22217a41f536f9faf65a1f1653","license":"MIT"}},"version":1}
