pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Pausable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract NuttyLoot is ERC721Enumerable, ERC721Pausable, IERC2981, AccessControl {
  struct BoxInfo {
    uint8 tier;
    uint id;
  }

  using Counters for Counters.Counter;
  Counters.Counter private _tokenIdTracker;
  uint16 public creatorRoyaltyBP;
  string public contractURI;
  string private _boxBaseURI;
  string private _itemBaseURI;
  address payable public royaltyTreasury;
  mapping(uint8 => uint[]) private _boxTierToIds;
  mapping(uint => uint[]) private _boxIdToItems;
  mapping(uint => BoxInfo) private _tokenIdToBoxInfo;
  mapping(uint => uint) private _tokenIdToItemId;

  event CreateBox(uint indexed id);
  event CreateItem(uint indexed id);
  event BalanceWithdrawl(uint indexed time);
  event DonationReceived();

  bytes32 public constant TICKETER = keccak256("TICKETER");
  bytes32 public constant ADMIN = keccak256("ADMIN");
  address payable private _owner;

  constructor(uint16 _creatorRoyaltyBP, string memory _contractURI, address payable _royaltyTreasury,
  address _ticketer, string memory boxBaseURI, string memory itemBaseURI)
  ERC721("NuttyLoot", "NLT") {
    require(_royaltyTreasury != address(0), "Royalty treasury is 0 address");
    require(_ticketer != address(0), "Royalty treasury is 0 address");

    creatorRoyaltyBP = _creatorRoyaltyBP;
    contractURI = _contractURI;
    royaltyTreasury = _royaltyTreasury;
    _boxBaseURI = boxBaseURI;
    _itemBaseURI = itemBaseURI;
    _pause();

    _setRoleAdmin(TICKETER, ADMIN);
    _setupRole(TICKETER, msg.sender);
    _setupRole(TICKETER, _ticketer);
    _setupRole(ADMIN, msg.sender);
    _owner = payable(msg.sender);
  }

  function royaltyInfo(uint256, uint256 salePrice) external view override(IERC2981) returns (address receiver, uint256 royaltyAmount) {
    require(salePrice > 0, "Invalid sale price");
    return (royaltyTreasury, (salePrice * creatorRoyaltyBP) / 10000);
  }

  function mintBox(address recipient, uint8 tier) public onlyRole(TICKETER) {
    require(tier > 0, "Invalid box tier");
    require(_boxTierToIds[tier].length > 0, "Boxes for selected tier not available");

    uint boxIdIndex = _boxTierToIds[tier].length - 1;
    uint newTokenId = _generateNewTokenId();
    _tokenIdToBoxInfo[newTokenId] = BoxInfo({ tier: tier, id: _boxTierToIds[tier][boxIdIndex] });
    _boxTierToIds[tier].pop();

    emit CreateBox(newTokenId);
    _safeMint(recipient, newTokenId);
  }

  function openBox(address recipient, uint tokenId) external {
    require(_exists(tokenId), "Token does not exist");
    require(ownerOf(tokenId) == msg.sender || hasRole(ADMIN, msg.sender), "Sender does not have permission to open box");
    require(isBox(tokenId), "Token is not a box");

    uint[] memory boxItems = _boxIdToItems[_tokenIdToBoxInfo[tokenId].id];
    require(boxItems.length > 0, "Box has no attached items");

    for(uint i = 0; i < boxItems.length; i++) {
      _mintItem(recipient, boxItems[i]);
    }

    delete _boxIdToItems[_tokenIdToBoxInfo[tokenId].id];
    _burn(tokenId);
  }

  function _mintItem(address recipient, uint itemId) private {
    uint newTokenId = _generateNewTokenId();
    _tokenIdToItemId[newTokenId] = itemId;
    emit CreateItem(newTokenId);
    _safeMint(recipient, newTokenId);
  }

  function _generateNewTokenId() private returns (uint) {
    _tokenIdTracker.increment();
    return _tokenIdTracker.current();
  }

  function isBox(uint tokenId) public view returns (bool) {
    return _tokenIdToBoxInfo[tokenId].id != 0;
  }

  function boxInfo(uint tokenId) public view returns (uint8 tier, uint boxId) {
    require(_exists(tokenId), "Token does not exist");
    require(isBox(tokenId), "Token is not a box");

    tier = _tokenIdToBoxInfo[tokenId].tier;
    boxId = _tokenIdToBoxInfo[tokenId].id;
  }

  function itemInfo(uint tokenId) public view returns (uint itemId) {
    require(_exists(tokenId), "Token does not exist");
    require(!isBox(tokenId), "Token is not a item");

    itemId = _tokenIdToItemId[tokenId];
  }

  function walletOfOwner(address ownerAddress) external view returns(uint[] memory) {
    uint tokenCount = balanceOf(ownerAddress);
    uint[] memory tokenIds = new uint[](tokenCount);

    for (uint i = 0; i < tokenCount; i++) {
      tokenIds[i] = tokenOfOwnerByIndex(ownerAddress, i);
    }

    return tokenIds;
  }

  function burn(uint tokenId) external onlyRole(ADMIN) { _burn(tokenId); }
  function setRoyaltyBP(uint16 basePoints) external onlyRole(ADMIN) { creatorRoyaltyBP = basePoints; }
  function setContractURI(string calldata metadataURI) external onlyRole(ADMIN) { contractURI = metadataURI; }
  function setBoxBaseURI(string calldata boxBaseURI) external onlyRole(ADMIN) { _boxBaseURI = boxBaseURI; }
  function setItemBaseURI(string calldata itemBaseURI) external onlyRole(ADMIN) { _itemBaseURI = itemBaseURI; }
  function setBoxTierIds(uint8 tier, uint[] memory boxIds) external onlyRole(ADMIN) { _boxTierToIds[tier] = boxIds; }
  function setBoxItems(uint boxId, uint[] memory itemIds) external onlyRole(ADMIN) { _boxIdToItems[boxId] = itemIds; }
  function setRoyaltyTreasury(address payable treasury) external onlyRole(ADMIN) {
    require(treasury != address(0), "Treasury is 0 address");
    royaltyTreasury = treasury;
  }
  function setPauseState(bool pauseState) external onlyRole(ADMIN) {
    if(pauseState && !paused()) { _pause(); }
    else if(!pauseState && paused()) { _unpause(); }
  }

  function withdrawAll() external payable onlyRole(ADMIN) {
    uint balance = address(this).balance;
    require(balance > 0);

    emit BalanceWithdrawl(block.timestamp);

    (bool success, ) = _owner.call{value: balance}("");
    require(success, "Withdraw failed");
  }

  function giveawayBox(address[] memory recipients, uint8 tier) external onlyRole(ADMIN) {
    for(uint16 i = 0; i < recipients.length; i++) {
      mintBox(recipients[i], tier);
    }
  }

  function tokenURI(uint256 tokenId) public view override(ERC721) returns (string memory) {
    require(_exists(tokenId), "URI query for nonexistent token");

    if(isBox(tokenId)) {
      return string(abi.encodePacked(_boxBaseURI, Strings.toString(_tokenIdToBoxInfo[tokenId].tier)));
    } else {
      return string(abi.encodePacked(_itemBaseURI, Strings.toString(_tokenIdToItemId[tokenId])));
    }
  }

  function _burn(uint256 tokenId) internal override(ERC721) {
    delete _tokenIdToBoxInfo[tokenId];
    delete _tokenIdToItemId[tokenId];
    super._burn(tokenId);
  }

  // override clashing extension functions
  function _beforeTokenTransfer(address from, address to, uint tokenId) internal override(ERC721Pausable, ERC721Enumerable) { super._beforeTokenTransfer(from, to, tokenId); }
  function supportsInterface(bytes4 interfaceId) public view virtual override(ERC721Enumerable, ERC721, IERC165, AccessControl) returns(bool) {
    return interfaceId == type(IERC2981).interfaceId || super.supportsInterface(interfaceId);
  }

  receive() external payable { emit DonationReceived(); }
}
